import Layout from "../components/Layout.js";
import fetch from "isomorphic-unfetch";
import Typing from 'react-typing-animation';
import Error from './_error';

const Post2 = ({errorCode, vehicle}) => {
    if(errorCode){
        return <Error statusCode={errorCode} />;
    }
    return(
    <Layout>
        <div className="black-background">
            <Typing>
            <h4>
               NAME: {vehicle.name}
            </h4>
            <p>MODEL: {vehicle.model}</p>
            <p>VEHICLE CLASS: {vehicle.vehicle_class}</p>
            <p>MANUFACTURER: {vehicle.manufacturer}</p>
            <p>LENGTH: {vehicle.length}</p>
            <p>MAX ATMOSPHERING SPEED: {vehicle.max_atmosphering_speed}</p>
            <p>CREW: {vehicle.crew}</p>
            <p>PASSENGERS: {vehicle.passengers}</p>
            <p>CARGO CAPACITY: {vehicle.cargo_capacity}</p>
            <p>Type any letter in the field and click enter to return</p>
            <input type="text" id="one" onKeyUp={handleKeyPress} />
            </Typing>
        </div>
    </Layout>
    )
    };

const handleKeyPress=(event)=>{
    if (event.key === 'Enter'){
        window.location.href="/vehicles"
    }
}

Post2.getInitialProps = async function(context) {
    const{id} = context.query;
    const res = await fetch(`https://swapi.co/api/vehicles/${id}`);
    const errorCode = res.statusCode > 200 ? res.statusCode : false
    const vehicle = await res.json();

    console.log(`Fetched vehicle: ${vehicle.name}`);

    return{errorCode, vehicle:vehicle};
};

export default Post2;
