import Layout from "../components/Layout.js";
import fetch from "isomorphic-unfetch";
import Typing from 'react-typing-animation';

const Post1 = props => (
    <Layout>
        <div className="black-background">
            <Typing>
            <h4>
                NAME: {props.starship.name}
            </h4>
            <p>MODEL: {props.starship.model}</p>
            <p>STARSHIP CLASS: {props.starship.starship_class}</p>
            <p>MANUFACTURER: {props.starship.manufacturer}</p>
            <p>LENGTH: {props.starship.length}</p>
            <p>MAX ATMOSPHERING SPEED: {props.starship.max_atmosphering_speed}</p>
            <p>CREW: {props.starship.crew}</p>
            <p>PASSENGERS: {props.starship.passengers}</p>
            <p>CARGO CAPACITY: {props.starship.cargo_capacity}</p>
            <p>HYPERDRIVE RATING: {props.starship.hyperdrive_rating}</p><br />
            <span>PRESS ESC to Return</span>
            <input type="text" id="one" onKeyUp={handleKeyPress} />
            </Typing>
        </div>
    </Layout>
);

const handleKeyPress=(event)=>{
    if (event.key === 'Enter'){
        window.location.href="/starships"
    }
}

Post1.getInitialProps = async function(context) {
    const{id} = context.query;
    const res = await fetch(`https://swapi.co/api/starships/${id}`);
    const starship = await res.json();

    console.log(`Fetched starship: ${starship.name}`);

    return{starship};
};

export default Post1;
