import Layout from "../components/Layout.js";
import fetch from "isomorphic-unfetch";
import Typing from 'react-typing-animation';
import Error from './_error';

const Post1 = ({errorCode, starship}) => {
    if(errorCode){
        return <Error statusCode={errorCode} />;
    }
    <Layout>
        <div className="black-background">
            <Typing>
            <h4>
                NAME: {starship.name}
            </h4>
            <p>MODEL: {starship.model}</p>
            <p>STARSHIP CLASS: {starship.starship_class}</p>
            <p>MANUFACTURER: {starship.manufacturer}</p>
            <p>LENGTH: {starship.length}</p>
            <p>MAX ATMOSPHERING SPEED: {starship.max_atmosphering_speed}</p>
            <p>CREW: {starship.crew}</p>
            <p>PASSENGERS: {starship.passengers}</p>
            <p>CARGO CAPACITY: {starship.cargo_capacity}</p>
            <p>HYPERDRIVE RATING: {starship.hyperdrive_rating}</p><br />
            <p>Type any letter in the field and click enter to return</p>
            <input type="text" id="one" onKeyUp={handleKeyPress} />
            </Typing>
        </div>
    </Layout>
    };

const handleKeyPress=(event)=>{
    if (event.key === 'Enter'){
        window.location.href="/starships"
    }
}

Post1.getInitialProps = async function(context) {
    const{id} = context.query;
    const res = await fetch(`https://swapi.co/api/starships/${id}`);
    const errorCode = res.statusCode > 200 ? res.statusCode : false
    const starship = await res.json();

    console.log(`Fetched starship: ${starship.name}`);

    return{errorCode, starship:starship};
};

export default Post1;
