import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Navbar from "../components/Navbar";
import Link from "next/Link";

const Starships = props => (
    <Layout>
        <div className="Starships-Container">
            <header>
                <Link href="/"><a><i className="fas fa-journal-whills fa-4x"></i></a></Link>
                <Navbar />
            </header>
            <h1>Starships, transports with hyperdrive capability </h1>
            <h2>Select for more information:</h2>
            <ul className="starship-list">
                {props.Starships.results.map((result,id)=> (
                    <li key={result.name}>
                        <Link href={`post1?id=${id}`}>
                            <a>{result.name}</a>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    </Layout>
);

Starships.getInitialProps = async function(){
    const res = await fetch("https://swapi.co/api/starships");
    const data = await res.json();

    return {
        Starships: data
    };
};

export default Starships;
