import css from 'styled-jsx/css'

export default css.global`
  *{
      margin: 0;
  }

  body {
    overflow-x: hidden;
    z-index: 1;
    margin: 0;
  }

  .fa-journal-whills{
    left:0;
  }

  .header-text{
    color: white;
    text-shadow: 2px 2px 4px yellow;
    font-size: 52px;
    text-align: center;
  }

  .header2-text{
    color: white;
    text-shadow: 2px 2px 4px yellow;
    font-size: 52px;
    text-align: center;
  }

  .nav-link{
    position:fixed;
    right: 0;
    z-index: 20;
  }

  nav li{
    display: inline-block;
  }

  .nav-link{
    cursor: pointer;
    text-decoration: none;
    color: white;
    opacity: .5;
    padding:1em;
  }

  .nav-link:hover{
    opacity: 1;
  }

  .header-section{
    min-height:100vh;
    background: url("/static/background3.jpg");
    background-size: cover;
    width: 100%;
  }

  .options-container{
    display: grid;
    grid-template-columns: 50% 50%;
  }

  .options-image{
    border: 1px solid #ddd;
    border-radius: 4px;
    padding: 10px 10px;
    margin: 15px 15px;
    display: block;
    margin-left: auto;
    margin-right: auto;
  }

  .options-image:hover{
    cursor: pointer;
    opacity: .5;
  }

  .options-text-position{
    transition: .5s ease;
    opacity: 1;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%)
  }

  .options-image:hover .options-text{
    opacity: 1;
  }
  .options-text{
    color: white;
    font-size: 20px;
  }

`
